mapper.select:
select c.DDI_Type 设备类型,count(distinct(a.package_no)) 出库包裹数 from t_storage_checkout_packages a left join study.t_device_sub_info b on b.DDI_No = a.device_no left join t_device_device_info c on b.DDI_No = c.DDI_No left join netfiance_db.t_shop_shop_info e on b.SHOP_No = e.SHOP_No LEFT JOIN netfiance_db.t_shop_agent_list f ON e.SHOP_AGUid = f.AGNT_Uid LEFT JOIN netfiance_db.t_shop_agent_top g ON f.AGNT_ATUid = g.ATOP_Uid left join study.t_shop_sub_info d on e.SHOP_No = d.SHOP_No left join (select DICT_Value,DICT_Name from netfiance_db.t_sys_dictionary_info where DICT_Field = 'shop_class' ) l on l.DICT_Value = e.SHOP_Class where DSI_Anti is null and a.created_at >= b.SHOP_No and (a.created_at <= b.DSI_Withdraw or b.DSI_Withdraw is null)
and date(a.created_at) >= #{filterMap.startDate}
and date(a.created_at) <= #{filterMap.endDate}
and g.ATOP_Title = #{filterMap.channelValue}
and c.DDI_Type = #{filterMap.deviceTypeValue}
and d.SHOP_Area = #{filterMap.areaValue}
and l.DICT_Name = #{filterMap.shopTypeValue}
#and g.ATOP_Title = {{选择渠道}} #and c.DDI_Type = {{选择设备类型}} #and d.SHOP_Area = '{{ 所属地区 }}' #and l.DICT_Name = '{{ 网点类型 }}'
group by DDI_Type

select c.DDI_Type 设备类型,count(distinct(a.package_no)) 在途包裹数 from t_storage_checkout_packages a left join study.t_device_sub_info b on b.DDI_No = a.device_no left join t_device_device_info c on b.DDI_No = c.DDI_No left join netfiance_db.t_shop_shop_info e on b.SHOP_No = e.SHOP_No LEFT JOIN netfiance_db.t_shop_agent_list f ON e.SHOP_AGUid = f.AGNT_Uid LEFT JOIN netfiance_db.t_shop_agent_top g ON f.AGNT_ATUid = g.ATOP_Uid left join study.t_shop_sub_info d on e.SHOP_No = d.SHOP_No left join (select DICT_Value,DICT_Name from netfiance_db.t_sys_dictionary_info where DICT_Field = 'shop_class' ) l on l.DICT_Value = e.SHOP_Class where DSI_Anti is null and a.created_at >= b.SHOP_No and (a.created_at <= b.DSI_Withdraw or b.DSI_Withdraw is null)
and a.created_at >= #{filterMap.startDate}
and a.created_at <= #{filterMap.endDate}
and g.ATOP_Title = #{filterMap.channelValue}
and c.DDI_Type = #{filterMap.deviceTypeValue}
and d.SHOP_Area = #{filterMap.areaValue}
and l.DICT_Name = #{filterMap.shopTypeValue}
#and g.ATOP_Title = {{选择渠道}} #and c.DDI_Type = {{选择设备类型}} #and d.SHOP_Area = '{{ 所属地区 }}' #and l.DICT_Name = '{{ 网点类型 }}'
group by DDI_Type
