mapper.select:
select g.ATOP_Title 顶级渠道,date_format(a.CreateDate,'%Y-%m') 月份,sum(a.ODER_Money) 销售额 from t_shop_order_info a left join t_device_device_info h on a.ODER_DUid = h.DDI_Uid left join study.t_device_sub_info d on h.DDI_No = d.DDI_No left join netfiance_db.t_shop_shop_info i on a.ODER_SPUid = i.SHOP_Uid LEFT JOIN netfiance_db.t_shop_agent_list f ON i.SHOP_AGUid = f.AGNT_Uid LEFT JOIN netfiance_db.t_shop_agent_top g ON f.AGNT_ATUid = g.ATOP_Uid where a.ODER_State = 0 and a.CreateDate >= d.DSI_Try and (a.CreateDate <= d.DSI_Withdraw or d.DSI_Withdraw is null) and d.DSI_Anti is null and date_format(a.CreateDate,'%Y-%m') >= #{filterMap.startMonth} and date_format(a.CreateDate,'%Y-%m') <= #{filterMap.endMonth}
and g.ATOP_Title = #{filterMap.channelValue}
group by 顶级渠道,月份

select count(*) from (
select g.ATOP_Title 顶级渠道,g.ATOP_ShopBonus 门店分成比例,g.ATOP_AllBonus 全部分成比例,(g.ATOP_AllBonus-g.ATOP_ShopBonus) 渠道分成比例
from netfiance_db.t_shop_agent_top g
group by 顶级渠道
) temp;
